import{_ as a,c as o,G as n,o as s}from"./chunks/framework.NR_SbArQ.js";const c=JSON.parse('{"title":"白学现场：在钢琴与歌声交织的修罗场中寻找教育的真物","description":"","frontmatter":{},"headers":[],"relativePath":"rape-3.md","filePath":"rape-3.md"}'),r={name:"rape-3.md"};function l(e,t,i,p,d,u){return s(),o("div",null,t[0]||(t[0]=[n(`<h1 id="白学现场-在钢琴与歌声交织的修罗场中寻找教育的真物" tabindex="-1">白学现场：在钢琴与歌声交织的修罗场中寻找教育的真物 <a class="header-anchor" href="#白学现场-在钢琴与歌声交织的修罗场中寻找教育的真物" aria-label="Permalink to &quot;白学现场：在钢琴与歌声交织的修罗场中寻找教育的真物&quot;">​</a></h1><h2 id="【南武学园の三回啊三回-雪菜の叹息与和纱の琴声-】" tabindex="-1">【南武学园の三回啊三回 ～雪菜の叹息与和纱の琴声～】 <a class="header-anchor" href="#【南武学园の三回啊三回-雪菜の叹息与和纱の琴声-】" aria-label="Permalink to &quot;【南武学园の三回啊三回 ～雪菜の叹息与和纱の琴声～】&quot;">​</a></h2><blockquote><p>&quot;为什么会变成这样呢...第一次有了历史名校的头衔，第一次有学生被沼稻田录取。两件快乐事情重合在一起，本应带来更多的快乐。但是，为什么，会变成这样呢...&quot;<br> ——某不愿透露姓名的冬马派校董</p></blockquote><h3 id="学园基本设定" tabindex="-1">学园基本设定 <a class="header-anchor" href="#学园基本设定" aria-label="Permalink to &quot;学园基本设定&quot;">​</a></h3><ul><li><strong>校名</strong>：南武学园（前身：斯大林圣彼得堡音乐教室）</li><li><strong>理事长</strong>：北原春希（自称&quot;只是普通の执行委员长&quot;）</li><li><strong>派阀斗争</strong>： <ul><li>雪菜派系：&quot;沼稻田合格率114.514%的优等生集团&quot;</li><li>和纱派系：&quot;钢琴室里永远错过的1919810次对视&quot;</li></ul></li><li><strong>著名校友</strong>： <ul><li>冬马曜子（国际奥赛金牌/钢琴国际大赛冠军）</li><li>小木曾雪菜（本大医学部首席/甲子园应援团长）</li><li>杉浦小春（轻音部现役王牌/学园祭执行委员）</li></ul></li></ul><h3 id="学园编年史" tabindex="-1">学园编年史 <a class="header-anchor" href="#学园编年史" aria-label="Permalink to &quot;学园编年史&quot;">​</a></h3><p><strong>1905.3</strong><br> &quot;为什么弹钢琴的手要用来写数学题啊Kora！&quot;<br> 初代校长饭冢武也于沙，，寺旁创立&quot;音乐与学业两手抓，两手都要硬（意味深）&quot;的不可思议学园</p><p><strong>大正浪漫时代</strong><br> &quot;北有沼附，南有南武&quot;的传说开始流传<br> 体育仓库里开始出现&quot;教えて教えてよ&quot;的涂鸦</p><p><strong>昭和の激震</strong><br> 改名为&quot;本部都立白学实验学校&quot;期间<br> 天台上每日上演&quot;届かない恋&quot;的经典场景（悲）</p><p><strong>平成の悲愿</strong><br><strong>CCAE</strong>特进班（<strong>C</strong>obato <strong>C</strong>lass <strong>A</strong>dvanced <strong>E</strong>ducation）成立<br> 每年学园祭必定上演《无法传达的爱恋》舞台剧（绝望）</p><h3 id="现在的修罗场" tabindex="-1">现在的修罗场 <a class="header-anchor" href="#现在的修罗场" aria-label="Permalink to &quot;现在的修罗场&quot;">​</a></h3><h4 id="教室分布图" tabindex="-1">[教室分布图] <a class="header-anchor" href="#教室分布图" aria-label="Permalink to &quot;[教室分布图]&quot;">​</a></h4><table tabindex="0"><thead><tr><th>区域</th><th>代表人物</th><th>标志物</th></tr></thead><tbody><tr><td>北校舍</td><td>冬马和纱</td><td>永远擦肩而过的钢琴室</td></tr><tr><td>南校舍</td><td>小木曾雪菜</td><td>天台の应许之地</td></tr><tr><td>中庭</td><td>北原春希</td><td>被撕成两半的志愿调查表</td></tr></tbody></table><h4 id="部活战争" tabindex="-1">[部活战争] <a class="header-anchor" href="#部活战争" aria-label="Permalink to &quot;[部活战争]&quot;">​</a></h4><ol><li><strong>轻音乐同好会</strong><br> &quot;三回啊三回！今年学园祭的压轴节目绝对要...&quot;</li><li><strong>应援团</strong><br> &quot;即使喉咙唱哑也要为你应援什么的，真是太狡猾了&quot;</li><li><strong>执行委员会</strong><br> &quot;为什么你总是能这么熟练地安排节目顺序啊！&quot;</li></ol><h3 id="特色教学" tabindex="-1">特色教学 <a class="header-anchor" href="#特色教学" aria-label="Permalink to &quot;特色教学&quot;">​</a></h3><p><strong>思维课堂の三原罪</strong>：</p><ol><li>在图书馆偶遇的概率：114514%</li><li>天台谈话的延迟满足：1919810秒</li><li>保健室病床的余温残留：十分甚至九分</li></ol><p><strong>特殊课程</strong>：</p><ul><li><strong>钢琴鉴赏</strong>：&quot;这个音色...事故意弹错的罢？&quot;</li><li><strong>声乐训练</strong>：&quot;届かない歌なんて、存在しない！&quot;</li><li><strong>心理辅导</strong>：&quot;你们都是我的翅膀什么的，绝对很奇怪啊！（半恼）&quot;</li></ul><h3 id="毕业生的选择" tabindex="-1">毕业生的选择 <a class="header-anchor" href="#毕业生的选择" aria-label="Permalink to &quot;毕业生的选择&quot;">​</a></h3><div class="language-flow vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">flow</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>st=&gt;start: 入学式</span></span>
<span class="line"><span>e=&gt;end: 毕业式</span></span>
<span class="line"><span>op1=&gt;operation: 加入雪菜派</span></span>
<span class="line"><span>op2=&gt;operation: 选择和纱派</span></span>
<span class="line"><span>cond=&gt;condition: 圣诞夜的抉择</span></span>
<span class="line"><span></span></span>
<span class="line"><span>st-&gt;cond</span></span>
<span class="line"><span>cond(yes)-&gt;op1-&gt;e</span></span>
<span class="line"><span>cond(no)-&gt;op2-&gt;e</span></span></code></pre></div><blockquote><p>&quot;即使再过100年，这份在钢琴室与天台之间往复的思念，也会继续传承下去罢...&quot;<br> ——某不愿透露姓名的图书管理员·柳原朋</p></blockquote><p>（※本设定集含有114514%的虚构要素，与真实存在的学校、人物无关。胃痛指数：十分甚至九分）</p>`,24)]))}const g=a(r,[["render",l]]);export{c as __pageData,g as default};
